mapper.insert:
insert into auction( auction_no, auctioneer_no, category_no, auction_title, auction_content, auction_closed_time, auction_opening_bid, auction_closing_bid, auction_bid_unit, auction_status, auction_latitude1, auction_longitude1, auction_circle1
,auction_latitude2, auction_longitude2, auction_circle2
) values( #{auctionNo}, #{auctioneerNo}, #{categoryNo}, #{auctionTitle}, #{auctionContent}, to_date(#{auctionClosedTime}, 'YYYY-MM-DD HH24:MI'), #{auctionOpeningBid}, #{auctionClosingBid}, #{auctionBidUnit}, #{auctionStatus}, #{auctionLatitude1}, #{auctionLongitude1}, #{auctionCircle1}
,#{auctionLatitude2}, #{auctionLongitude2}, #{auctionCircle2}
)

mapper.select:
select * from ( select rownum rn, TMP.* from ( select A.auction_no, A.auction_title, A.auction_upload_time, A.auction_private, M.member_nick, C.category_name, (select count(*) from auction_report AR where AR.auction_no = A.auction_no) "report_count" from auction A inner join member M on A.auctioneer_no = M.member_no inner join category C on A.category_no = C.category_no
where instr(${type}, #{keyword}) > 0
order by A.auction_no desc ) TMP ) where rn between #{begin} and #{end}
