mapper.select:
	select * from (
 		select rownum rn, TMP.* from (
 			select MR.*, M.member_name from manager_restriction MR inner join member M on MR.member_no = M.member_no


				 		where instr(${type}, #{keyword}) > 0


		 	order by restriction_no desc
 		) TMP
 	) where rn between #{begin} and #{end}
